## TDD: GREEN Phase - DO Subclass Generator

### Minimum Implementation

```typescript
// packages/codegen/src/generators/do.ts
import type { Schema, Resource } from '@saaskit/schema'
import { pascalCase, camelCase } from 'change-case'

export function generateDOSubclass(schema: Schema): string {
  const className = pascalCase(schema.name) + 'DO'
  const resources = Object.entries(schema.resources)

  return `
import { SaasKitDO } from '@saaskit/core'
import { z } from 'zod'

${resources.map(([name, r]) => generateZodSchema(name, r)).join('\n\n')}

export default class ${className} extends SaasKitDO {
  protected allowedMethods = new Set([
    ${generateAllowedMethods(resources).join(',\n    ')}
  ])

${resources.map(([name, r]) => generateResourceMethods(name, r)).join('\n\n')}

${generateRelationshipMethods(schema)}
}
`.trim()
}

function generateZodSchema(name: string, resource: Resource): string {
  const schemaName = pascalCase(name) + 'Schema'
  const fields = Object.entries(resource.fields || {})

  return `const ${schemaName} = z.object({
  ${fields.map(([f, def]) => generateZodField(f, def)).join(',\n  ')}
})`
}

function generateZodField(name: string, def: FieldDef): string {
  const zodType = fieldTypeToZod(def.type)
  const optional = def.required ? '' : '.optional()'
  return `${name}: ${zodType}${optional}`
}

function generateResourceMethods(name: string, resource: Resource): string {
  const singular = pascalCase(name.replace(/s$/, '')) // naive singularize
  const schemaName = pascalCase(name) + 'Schema'

  return `
  async create${singular}(data: z.input<typeof ${schemaName}>) {
    return this.create('${name}', ${schemaName}.parse(data))
  }

  async get${singular}(id: string) {
    return this.get('${name}', id)
  }

  async list${pascalCase(name)}(options?: ListOptions) {
    return this.list('${name}', options)
  }

  async update${singular}(id: string, data: Partial<z.input<typeof ${schemaName}>>) {
    return this.update('${name}', id, data)
  }

  async delete${singular}(id: string) {
    return this.delete('${name}', id)
  }
`
}

function generateAllowedMethods(resources: [string, Resource][]): string[] {
  const methods: string[] = []
  for (const [name] of resources) {
    const singular = pascalCase(name.replace(/s$/, ''))
    methods.push(
      `'create${singular}'`,
      `'get${singular}'`,
      `'list${pascalCase(name)}'`,
      `'update${singular}'`,
      `'delete${singular}'`
    )
  }
  return methods
}
```

