// packages/core/src/legacy/datastore-adapter.ts
import type { IDataStore } from './types'
import type { SaasKitDO } from '../saaskit-do'

export function createDOAdapter(do: SaasKitDO): IDataStore {
  const warn = () => console.warn(
    'IDataStore is deprecated. Use SaasKitDO directly.'
  )

  return {
    async create(collection, data) {
      warn()
      return do.create(collection, data)
    },
    async read(collection, id) {
      warn()
      return do.get(collection, id)
    },
    async update(collection, id, data) {
      warn()
      return do.update(collection, id, data)
    },
    async delete(collection, id) {
      warn()
      return do.delete(collection, id)
    },
    async list(collection, options) {
      warn()
      return do.list(collection, options)
    },
  }
}
