export async function migrateDataStore(
  source: IDataStore,
  target: SaasKitDO,
  collections: string[]
): Promise<void> {
  for (const collection of collections) {
    const items = await source.list(collection)
    for (const item of items) {
      await target.create(collection, item)
    }
  }
}
