import { DO } from '@dotdo/do'
import type { Schema, Resource } from '@saaskit/schema'
import { z } from 'zod'

export class SaasKitDO extends DO {
  readonly schema: Schema
  private validators: Map<string, z.ZodSchema>

  constructor(
    ctx: DurableObjectState,
    env: unknown,
    schema: Schema
  ) {
    super(ctx, env)
    this.schema = schema
    this.validators = this.buildValidators(schema)
    this.configureAllowedMethods()
  }

  private buildValidators(schema: Schema): Map<string, z.ZodSchema> {
    const validators = new Map()
    for (const [name, resource] of Object.entries(schema.resources)) {
      validators.set(name, resourceToZod(resource))
    }
    return validators
  }

  private configureAllowedMethods() {
    // Add schema-derived methods to allowlist
    for (const resource of Object.keys(this.schema.resources)) {
      // Resource-specific methods could be added here
    }
  }

  async create(collection: string, data: unknown) {
    this.validateCollection(collection)
    this.validateData(collection, data)
    return super.create(collection, data)
  }

  private validateCollection(collection: string) {
    if (!this.schema.resources[collection]) {
      throw new Error(`Unknown resource: ${collection}`)
    }
  }

  private validateData(collection: string, data: unknown) {
    const validator = this.validators.get(collection)
    if (validator) {
      validator.parse(data) // throws on invalid
    }
  }

  getSchema(): Schema {
    return this.schema
  }

  getResources(): string[] {
    return Object.keys(this.schema.resources)
  }
}
