// Generated: worker.ts
import MyAppDO from './my-app.do'

export { MyAppDO }

interface Env {
  MY_APP_DO: DurableObjectNamespace<MyAppDO>
}

export default {
  async fetch(request: Request, env: Env): Promise<Response> {
    const url = new URL(request.url)

    // Extract DO ID from path or use 'default'
    const doId = extractDoId(url) || 'default'
    const id = env.MY_APP_DO.idFromName(doId)
    const stub = env.MY_APP_DO.get(id)

    return stub.fetch(request)
  }
}

function extractDoId(url: URL): string | null {
  const match = url.pathname.match(/^\/do\/([^\/]+)/)
  return match ? match[1] : null
